INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (100, 1, 1, "ペコリーヌのプリン", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 3, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "やばいですね☆なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (101, 2, 1, "コッコロのプリン", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 3, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "主さま、なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (102, 3, 0, "キャルのプリン", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (103, 4, 1, "シェフィのプリン", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 3, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "おにーたん♪なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (104, 5, 1, "イリヤのプリン", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 3, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "ハーハッハッハ！\n世界征服なのじゃなのー！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (105, 6, 1, "アカリのプリン", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 3, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "お兄ちゃんのエッチ！　なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (106, 7, 1, "ヨリのプリン", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 3, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (107, 8, 0, "シノブのプリン", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 3, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (108, 9, 1, "ジュンのプリン", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 3, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "インフェルノシールド！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (109, 10, 1, "トモのプリン", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 3, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (110, 11, 1, "マツリのプリン", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 3, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "タイガーヒーローボンバー！\nなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (111, 13, 1, "クロエのプリン", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 3, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "ま、いいんじゃん？\nしらんけどなの", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (112, 14, 1, "チエルのプリン", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 3, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "ぷりーん★なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (113, 15, 1, "ユニのプリン", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 3, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "端的に換言すれば\n青春の味なの？", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (114, 16, 1, "サレンのプリン", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 3, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "幼なじみはあんた…なの？", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (115, 17, 1, "スズメのプリン", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 3, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "ひゃああんなの～！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (116, 18, 0, "クルミのプリン", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 3, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "サルキデバレヤ！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (117, 19, 1, "アヤネのプリン", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 3, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "プリンフルスイングなの～！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (118, 20, 1, "マホのプリン", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 3, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "みらくるみやりん\nくるりんぱ～☆なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (119, 21, 1, "マコトのプリン", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 3, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "ウルフェンバイトなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (120, 22, 1, "カオリのプリン", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 3, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "なんくるないさ～！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (121, 23, 1, "カスミのプリン", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 3, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "ふふん。簡単な推理なの", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (122, 24, 1, "マヒルのプリン", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 3, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "エリザベス～！なの～！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (123, 25, 1, "シオリのプリン", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 3, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (124, 26, 1, "リマのプリン", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 3, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "もふもふにしてあげるなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (125, 27, 0, "リンのプリン", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 3, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "めんどくさいな～なの…", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (126, 28, 1, "ラビリスタのプリン", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 3, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "プリンの権能を解放するの～！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (127, 29, 0, "シズルのプリン", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 3, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "お姉ちゃんパワー全開！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (128, 30, 1, "リノのプリン", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 3, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "お兄ちゃん、プリンをよこすの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (129, 31, 1, "ヒヨリのプリン", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 3, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "困った時はお互いさまさま！\nなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (130, 32, 1, "ユイのプリン", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 3, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "フラワリングヒールなの～♪", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (131, 33, 1, "レイのプリン", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 3, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "スラッシュテンペスト！なの。", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (132, 34, 1, "アキノのプリン", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 3, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "おーほっほほほ！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (133, 35, 1, "ユカリのプリン", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 3, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "プリしゅわ最高～！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (134, 36, 1, "タマキのプリン", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 3, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "にゃにゃにゃ！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (135, 37, 0, "ミフユのプリン", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 3, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "効率全開！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (136, 38, 1, "ミサトのプリン", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 3, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "あらあら～なの～♪", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (137, 39, 1, "ハツネのプリン", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 3, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "スヤスヤ～…なの…", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (138, 40, 1, "アオイのプリン", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 3, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "ぼっちは卒業なの～！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (139, 41, 1, "ノゾミのプリン", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 3, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "リズムあげるのー！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (140, 42, 1, "チカのプリン", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 3, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "風の加護を！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (141, 43, 1, "ツムギのプリン", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 3, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "騎士さんのために\nしたんじゃありません！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (142, 12, 0, "クリスティーナのプリン", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 3, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "宴の始まりなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (143, 44, 1, "ルカのプリン", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 3, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "一刀両断、覚悟しな！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (144, 45, 1, "ナナカのプリン", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 3, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "きゅぴーん！キタコレ！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (145, 46, 1, "アンナのプリン", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 3, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "人呼んで、疾風のミヤコなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (146, 47, 1, "エリコのプリン", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 3, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "クスクス…クスクスクス…なの", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (147, 48, 1, "ミツキのプリン", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 3, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (148, 49, 1, "モニカのプリン", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 3, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "私は子どもではない！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (149, 50, 1, "ニノンのプリン", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 3, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (150, 51, 0, "クウカのプリン", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 3, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "…ぐふふ、じゅるり～なの", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (151, 52, 1, "ユキのプリン", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 3, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "あぁ、ミヤコはなんて美しいの～", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (152, 53, 0, "アユミのプリン", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 3, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (153, 54, 0, "ミソギのプリン", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 3, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "にひひ…イタズラの時間なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (154, 55, 0, "ミミのプリン", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 3, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "ぴょんぴょこぴょーん、なの♪", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (155, 56, 0, "キョウカのプリン", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 3, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "へんたいふしんしゃさんなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (156, 57, 1, "スズナのプリン", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 3, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "ハート狙い撃ち！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (157, 58, 0, "イオのプリン", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 3, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "授業を始めます♪なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (158, 59, 1, "ミサキのプリン", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 3, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "立派なレディ、なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (159, 60, 1, "ホマレのプリン", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 3, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (160, 61, 1, "カヤのプリン", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 3, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "たぎってきたの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (161, 62, 0, "イノリのプリン", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 3, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "下剋上、やってやるですなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (162, 63, 1, "ムイミのプリン", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 3, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "どけどけ～！なの～！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (163, 64, 1, "ネネカのプリン", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 3, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "ミラーミラー！なの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (164, 65, 1, "クレジッタのプリン", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 3, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "プリンに忠誠を誓いますわ！\nなの！", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (165, 66, 1, "カリンのプリン", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 3, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "ギルド管理協会へようこそ！\nなの！", 5);
