INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 5, "ペコリーヌのプリン", "やばいですね☆なの！", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 1, 3, 100, 1);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 5, "コッコロのプリン", "主さま、なの！", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 1, 3, 101, 2);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 5, "キャルのプリン", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 0, 3, 102, 3);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 5, "シェフィのプリン", "おにーたん♪なの！", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 1, 3, 103, 4);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 5, "イリヤのプリン", "ハーハッハッハ！\n世界征服なのじゃなのー！", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 1, 3, 104, 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 5, "アカリのプリン", "お兄ちゃんのエッチ！　なの！", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 1, 3, 105, 6);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 5, "ヨリのプリン", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 1, 3, 106, 7);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 5, "シノブのプリン", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 0, 3, 107, 8);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 5, "ジュンのプリン", "インフェルノシールド！なの！", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 1, 3, 108, 9);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 5, "トモのプリン", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 1, 3, 109, 10);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 5, "マツリのプリン", "タイガーヒーローボンバー！\nなの！", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 1, 3, 110, 11);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 5, "クロエのプリン", "ま、いいんじゃん？\nしらんけどなの", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 1, 3, 111, 13);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 5, "チエルのプリン", "ぷりーん★なの！", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 1, 3, 112, 14);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 5, "ユニのプリン", "端的に換言すれば\n青春の味なの？", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 1, 3, 113, 15);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 5, "サレンのプリン", "幼なじみはあんた…なの？", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 1, 3, 114, 16);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 5, "スズメのプリン", "ひゃああんなの～！", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 1, 3, 115, 17);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 5, "クルミのプリン", "サルキデバレヤ！なの！", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 0, 3, 116, 18);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 5, "アヤネのプリン", "プリンフルスイングなの～！", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 1, 3, 117, 19);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 5, "マホのプリン", "みらくるみやりん\nくるりんぱ～☆なの！", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 1, 3, 118, 20);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 5, "マコトのプリン", "ウルフェンバイトなの！", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 1, 3, 119, 21);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 5, "カオリのプリン", "なんくるないさ～！なの！", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 1, 3, 120, 22);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 5, "カスミのプリン", "ふふん。簡単な推理なの", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 1, 3, 121, 23);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 5, "マヒルのプリン", "エリザベス～！なの～！", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 1, 3, 122, 24);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 5, "シオリのプリン", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 1, 3, 123, 25);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 5, "リマのプリン", "もふもふにしてあげるなの！", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 1, 3, 124, 26);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 5, "リンのプリン", "めんどくさいな～なの…", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 0, 3, 125, 27);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 5, "ラビリスタのプリン", "プリンの権能を解放するの～！", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 1, 3, 126, 28);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 5, "シズルのプリン", "お姉ちゃんパワー全開！なの！", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 0, 3, 127, 29);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 5, "リノのプリン", "お兄ちゃん、プリンをよこすの！", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 1, 3, 128, 30);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 5, "ヒヨリのプリン", "困った時はお互いさまさま！\nなの！", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 1, 3, 129, 31);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 5, "ユイのプリン", "フラワリングヒールなの～♪", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 1, 3, 130, 32);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 5, "レイのプリン", "スラッシュテンペスト！なの。", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 1, 3, 131, 33);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 5, "アキノのプリン", "おーほっほほほ！なの！", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 1, 3, 132, 34);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 5, "ユカリのプリン", "プリしゅわ最高～！なの！", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 1, 3, 133, 35);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 5, "タマキのプリン", "にゃにゃにゃ！なの！", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 1, 3, 134, 36);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 5, "ミフユのプリン", "効率全開！なの！", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 0, 3, 135, 37);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 5, "ミサトのプリン", "あらあら～なの～♪", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 1, 3, 136, 38);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 5, "ハツネのプリン", "スヤスヤ～…なの…", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 1, 3, 137, 39);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 5, "アオイのプリン", "ぼっちは卒業なの～！", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 1, 3, 138, 40);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 5, "ノゾミのプリン", "リズムあげるのー！", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 1, 3, 139, 41);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 5, "チカのプリン", "風の加護を！なの！", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 1, 3, 140, 42);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 5, "ツムギのプリン", "騎士さんのために\nしたんじゃありません！なの！", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 1, 3, 141, 43);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 5, "クリスティーナのプリン", "宴の始まりなの！", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 0, 3, 142, 12);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 5, "ルカのプリン", "一刀両断、覚悟しな！なの！", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 1, 3, 143, 44);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 5, "ナナカのプリン", "きゅぴーん！キタコレ！なの！", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 1, 3, 144, 45);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 5, "アンナのプリン", "人呼んで、疾風のミヤコなの！", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 1, 3, 145, 46);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 5, "エリコのプリン", "クスクス…クスクスクス…なの", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 1, 3, 146, 47);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 5, "ミツキのプリン", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 1, 3, 147, 48);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 5, "モニカのプリン", "私は子どもではない！なの！", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 1, 3, 148, 49);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 5, "ニノンのプリン", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 1, 3, 149, 50);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 5, "クウカのプリン", "…ぐふふ、じゅるり～なの", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 0, 3, 150, 51);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 5, "ユキのプリン", "あぁ、ミヤコはなんて美しいの～", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 1, 3, 151, 52);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 5, "アユミのプリン", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 0, 3, 152, 53);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 5, "ミソギのプリン", "にひひ…イタズラの時間なの！", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 0, 3, 153, 54);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 5, "ミミのプリン", "ぴょんぴょこぴょーん、なの♪", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 0, 3, 154, 55);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 5, "キョウカのプリン", "へんたいふしんしゃさんなの！", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 0, 3, 155, 56);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 5, "スズナのプリン", "ハート狙い撃ち！なの！", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 1, 3, 156, 57);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 5, "イオのプリン", "授業を始めます♪なの！", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 0, 3, 157, 58);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 5, "ミサキのプリン", "立派なレディ、なの！", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 1, 3, 158, 59);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 5, "ホマレのプリン", "プリンを持ってこないと\nお仕置きだよ～☆なの！", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 1, 3, 159, 60);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 5, "カヤのプリン", "たぎってきたの！", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 1, 3, 160, 61);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 5, "イノリのプリン", "下剋上、やってやるですなの！", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 0, 3, 161, 62);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 5, "ムイミのプリン", "どけどけ～！なの～！", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 1, 3, 162, 63);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 5, "ネネカのプリン", "ミラーミラー！なの！", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 1, 3, 163, 64);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 5, "クレジッタのプリン", "プリンに忠誠を誓いますわ！\nなの！", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 1, 3, 164, 65);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES ("手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 5, "カリンのプリン", "ギルド管理協会へようこそ！\nなの！", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 1, 3, 165, 66);
