INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "やばいですね☆なの！", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1, 100, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 5, "ペコリーヌのプリン", 1);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "主さま、なの！", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 1, 101, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 5, "コッコロのプリン", 2);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 0, 102, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 5, "キャルのプリン", 3);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "おにーたん♪なの！", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 1, 103, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 5, "シェフィのプリン", 4);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ハーハッハッハ！\n世界征服なのじゃなのー！", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 1, 104, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 5, "イリヤのプリン", 5);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "お兄ちゃんのエッチ！　なの！", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 1, 105, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 5, "アカリのプリン", 6);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 1, 106, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 5, "ヨリのプリン", 7);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 0, 107, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 5, "シノブのプリン", 8);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "インフェルノシールド！なの！", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 1, 108, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 5, "ジュンのプリン", 9);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 1, 109, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 5, "トモのプリン", 10);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "タイガーヒーローボンバー！\nなの！", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 1, 110, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 5, "マツリのプリン", 11);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ま、いいんじゃん？\nしらんけどなの", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 1, 111, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 5, "クロエのプリン", 13);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ぷりーん★なの！", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 1, 112, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 5, "チエルのプリン", 14);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "端的に換言すれば\n青春の味なの？", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 1, 113, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 5, "ユニのプリン", 15);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "幼なじみはあんた…なの？", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 1, 114, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 5, "サレンのプリン", 16);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ひゃああんなの～！", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 1, 115, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 5, "スズメのプリン", 17);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "サルキデバレヤ！なの！", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 0, 116, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 5, "クルミのプリン", 18);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "プリンフルスイングなの～！", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 1, 117, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 5, "アヤネのプリン", 19);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "みらくるみやりん\nくるりんぱ～☆なの！", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 1, 118, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 5, "マホのプリン", 20);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ウルフェンバイトなの！", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 1, 119, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 5, "マコトのプリン", 21);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "なんくるないさ～！なの！", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 1, 120, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 5, "カオリのプリン", 22);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ふふん。簡単な推理なの", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 1, 121, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 5, "カスミのプリン", 23);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "エリザベス～！なの～！", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 1, 122, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 5, "マヒルのプリン", 24);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 1, 123, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 5, "シオリのプリン", 25);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "もふもふにしてあげるなの！", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 1, 124, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 5, "リマのプリン", 26);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "めんどくさいな～なの…", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 0, 125, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 5, "リンのプリン", 27);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "プリンの権能を解放するの～！", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 1, 126, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 5, "ラビリスタのプリン", 28);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "お姉ちゃんパワー全開！なの！", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 0, 127, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 5, "シズルのプリン", 29);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "お兄ちゃん、プリンをよこすの！", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 1, 128, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 5, "リノのプリン", 30);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "困った時はお互いさまさま！\nなの！", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 1, 129, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 5, "ヒヨリのプリン", 31);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "フラワリングヒールなの～♪", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 1, 130, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 5, "ユイのプリン", 32);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "スラッシュテンペスト！なの。", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 1, 131, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 5, "レイのプリン", 33);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "おーほっほほほ！なの！", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 1, 132, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 5, "アキノのプリン", 34);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "プリしゅわ最高～！なの！", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 1, 133, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 5, "ユカリのプリン", 35);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "にゃにゃにゃ！なの！", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 1, 134, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 5, "タマキのプリン", 36);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "効率全開！なの！", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 0, 135, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 5, "ミフユのプリン", 37);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "あらあら～なの～♪", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 1, 136, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 5, "ミサトのプリン", 38);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "スヤスヤ～…なの…", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 1, 137, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 5, "ハツネのプリン", 39);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ぼっちは卒業なの～！", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 1, 138, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 5, "アオイのプリン", 40);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "リズムあげるのー！", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 1, 139, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 5, "ノゾミのプリン", 41);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "風の加護を！なの！", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 1, 140, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 5, "チカのプリン", 42);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "騎士さんのために\nしたんじゃありません！なの！", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 1, 141, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 5, "ツムギのプリン", 43);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "宴の始まりなの！", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 0, 142, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 5, "クリスティーナのプリン", 12);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "一刀両断、覚悟しな！なの！", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 1, 143, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 5, "ルカのプリン", 44);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "きゅぴーん！キタコレ！なの！", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 1, 144, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 5, "ナナカのプリン", 45);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "人呼んで、疾風のミヤコなの！", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 1, 145, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 5, "アンナのプリン", 46);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "クスクス…クスクスクス…なの", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 1, 146, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 5, "エリコのプリン", 47);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 1, 147, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 5, "ミツキのプリン", 48);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "私は子どもではない！なの！", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 1, 148, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 5, "モニカのプリン", 49);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 1, 149, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 5, "ニノンのプリン", 50);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "…ぐふふ、じゅるり～なの", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 0, 150, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 5, "クウカのプリン", 51);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "あぁ、ミヤコはなんて美しいの～", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 1, 151, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 5, "ユキのプリン", 52);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 0, 152, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 5, "アユミのプリン", 53);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "にひひ…イタズラの時間なの！", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 0, 153, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 5, "ミソギのプリン", 54);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ぴょんぴょこぴょーん、なの♪", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 0, 154, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 5, "ミミのプリン", 55);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "へんたいふしんしゃさんなの！", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 0, 155, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 5, "キョウカのプリン", 56);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ハート狙い撃ち！なの！", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 1, 156, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 5, "スズナのプリン", 57);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "授業を始めます♪なの！", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 0, 157, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 5, "イオのプリン", 58);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "立派なレディ、なの！", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 1, 158, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 5, "ミサキのプリン", 59);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 1, 159, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 5, "ホマレのプリン", 60);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "たぎってきたの！", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 1, 160, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 5, "カヤのプリン", 61);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "下剋上、やってやるですなの！", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 0, 161, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 5, "イノリのプリン", 62);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "どけどけ～！なの～！", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 1, 162, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 5, "ムイミのプリン", 63);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ミラーミラー！なの！", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 1, 163, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 5, "ネネカのプリン", 64);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "プリンに忠誠を誓いますわ！\nなの！", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 1, 164, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 5, "クレジッタのプリン", 65);
INSERT INTO `v1_f9905d64e89b6bc403296d29946053d20de8a3a2e8e220e0ddbc9b1beef75bac` VALUES (3, "ギルド管理協会へようこそ！\nなの！", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 1, 165, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 5, "カリンのプリン", 66);
