INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 200, 1, "ヒヨリの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理大ダメージを与え、\n　その周りの狭い範囲の敵すべてにも\n　物理中ダメージを与える。\n・目の前の敵１キャラに物理中ダメージを\n　与える。\n・目の前の敵１キャラに物理小ダメージを与え、\n　さらに自分の物理攻撃力を中アップさせる。", 270, 0, 206700, 2.295, 0, 0, 206700, "ヒヨリのシャドウ");
