INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 80, 0, "【物理】近距離攻撃\n巨大なハサミと硬い殻が自慢の\nカニの魔物。砂に潜んで気長に獲物\nを待つ姿はどこか愛らしい。", 270, 0, 213700, 2.0, 0, 0, 213700, "スパイククラブ");
INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 210, 0, "【物理】近接攻撃\n熱帯魚のカタチをした魚の魔物。\nその優雅な姿とは裏腹に、鋭いアゴ\nを獲物めがけて突き立てる。", 270, 0, 213800, 10.0, 0, 0, 213800, "エンゼルダーツ");
INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 650, 1, "【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。", 270, 0, 213900, 3.5, 0, 0, 213900, "ギャングオッター");
INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 2, 800, 0, "【魔法】範囲攻撃\n頭部の石から発する超音波で\n巧みに狩りをするエイの魔物。", 270, 0, 214000, 3.5, 0, 0, 214000, "マジックレイ");
INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 200, 15, "【物理】前衛・近接攻撃\nしなやかな枝を、ムチのように\n使い攻撃する木の魔物。", 270, 0, 214100, 2.5, 0, 0, 214100, "スタンプ");
