INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 240, 2, "ムイミの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理特大ダメージを与え\n　る。さらにＴＰが無くなるまでの間天楼覇断剣\n　を装備する。\n・前方範囲内の敵すべてに物理小ダメージを与え、\n　さらにスタンさせて小さく吹き飛ばす。\n・前方範囲内の敵すべての物理防御力を小ダウン\n　させる。", 450, 0, 214901, 1.8, 0, 0, 214901, "ムイミのシャドウ");
INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 320, 1, "カヤの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理特大ダメージを与え\n　る。バトル開始後、初弾のユニオンバーストは\n　ダメージ量が大幅アップ。\n・目の前の敵１キャラに物理中ダメージを与える。\n・自分の物理攻撃力と物理クリティカルを大アッ\n　プさせる。", 450, 0, 215001, 2.17, 0, 0, 215001, "カヤのシャドウ");
