INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 1000, 16, "【物理】海辺に君臨する巨大な軟体魔獣。\n強靭な触手と鋼鉄の大銛で全てを破壊しつくす。\n・敵全体にキャラの最大ＨＰに応じた\n　物理大ダメージを与える。\n・後方範囲内に魔法中ダメージを与え、\n　麻痺状態を付与、さらに物理攻撃力、\n　魔法攻撃力を特大ダウン。\n・一定時間経過すると敵単体に\n　物理超特大ダメージを与える。\n・テンタパス自身のＨＰが40％を切ると、\n　さらに攻撃が激しく凶暴になる。", 270, 0, 304300, 1.8, 0, 0, 304300, "テンタパス");
