INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 1000, 16, "【物理】塔の力で人間に復讐を目論む、軟体魔獣。\n若かりし頃に刺された銛の恨みは、海よりも深い。\n\n・敵全体に物理大ダメージを与える。\n・前から３番目以降の敵に魔法中ダメージを与え、\n　物理防御力と行動速度を中ダウン。\n　さらに、ステータスアップ効果を解除する。\n・残りＨＰの最も高い敵に、残りＨＰに応じた\n　物理大ダメージを与え、ＴＰを中ダウン。", 270, 0, 304304, 1.6, 0, 0, 304304, "オクタスキッド");
