INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 1000, 3, "【物理】塔の魔力を喰らい、暴れ回る巨大魔鳥。\n迂闊に近づくものは、暴風と雷豪が身を引き裂く。\n\n・ユニオンバーストは、敵全体に物理大ダメージ\n　を与える。\n・前から２番目のキャラを中心にした小範囲に物\n　理小ダメージを与え、スタンさせる。\n・前方範囲内の敵に物理中ダメージを与える。", 270, 0, 307504, 1.8, 0, 0, 307504, "紅極鳥");
