INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 3000, 16, "【物理】規格外のアイデアと歪んだ情熱から爆誕\nした鋼鉄の巨人。礎となった者の特性を、異常な\n解釈で再構築し敵の言語中枢と精神を破壊する。\n\n・自身が毒、猛毒、呪い、呪詛、火傷の効果を\n　受けると、自身の物理防御力と魔法防御力と\n　行動速度が大ダウンする。\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを与える。\n・自身の物理攻撃力をアップさせたあと、\n　敵全体に物理ダメージを与える。\n・前方３キャラに最大ＨＰに応じた魔法割合\n　ダメージを与え、行動速度をダウンさせる。", 270, 0, 310700, 1.4, 0, 0, 310701, "なかよしＸ");
