INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 3000, 16, "【ＭＯＤＥ２】魔法少女たちの猛攻をも取り込み\nさらに膨れ上がるアンチビーストの渇望。更には\n人々を守るための勇気までも溶蝕されてゆく。\n\n・ユニオンバーストは、敵全体のバリアを解除し、\n　魔法大ダメージを与える。さらに、物理防御力\n　と魔法防御力を大ダウンさせ、一定時間ＨＰ回\n　復量を70％ダウンさせる。\n・敵全体のバリアを解除し、最大ＨＰに応じた\n　魔法割合ダメージを与え、毒状態にする。", 270, 0, 312100, 2.7, 0, 0, 312105, "アンチビースト de M");
