INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 2, 3000, 16, "【ＭＯＤＥ１】第一の試練。黒き風の精霊の暴風\nの継ぎ目を見切れば、自ずと己の進むべき道が\n切り拓かれん。\n\n・敵全体に物理ダメージとスタンを与え、ノック\n　バックさせる。\n　黒風の精霊が眠っている間、自身の物理防御力\n　が大幅にダウンし、その間に100000以上\n　のダメージを与えると、防御力ダウンが延長さ\n　れ行動速度もダウンする。", 270, 0, 312700, 2.7, 0, 0, 312704, "黒風の精霊");
