INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 2, 3000, 16, "【ＭＯＤＥ３】第三の試練。終の試練は、己の\nすべてを懸け黒き風の精霊に本当の想いと絆を\n顕現し、己の想いを確かめよ。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\n　力を大ダウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 270, 0, 312700, 2.7, 0, 0, 312706, "黒風の精霊");
