INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 2, 3000, 16, "【ＭＯＤＥ１】暴走した魔道具は、すべてのもの\nを自身の領域に留めようとする。幾重もの障壁に\nも屈せず、己を信じ未来を切り拓け。\n\n・このバトルでは、全てのターゲットがブレイク\n　無効。\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージを与えるたびに徐々に耐久性\n　を失っていき、一定量のダメージを与えるとバ\n　リアは破壊される。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、さらに行動速度を中ダウン、魔法防御\n　力を中ダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・自分の魔法攻撃力を中アップ、行動速度を大\n　アップさせる。", 270, 0, 313104, 1.4, 0, 0, 313104, "バースト・タイムラーベ");
