INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 2, 3000, 16, "【ＭＯＤＥ２】現れたのはリリ達のシャドウ。\n降りかかる刃が、忌まわしき過去との対峙を迫る。\nだが打ち克たなければ、前には進めない…！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ミソラから受けるすべてのダメージで発生する、\n　パーティキャラのＴＰ回復量を中ダウンさせる。\n・前から１番目、２番目、３番目の敵に魔法小ダ\n　メージを与える。", 270, 0, 313706, 2.0, 0, 0, 313706, "ミソラ");
