INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 3000, 16, "常に幾つもの奥の手を隠し持つ、大食の紳士。\n万能人形を駆使し、知略戦略で敵を追い詰める。\n\n・ユニオンバーストは、敵全体にアゾールドの物\n　理攻撃力に応じた物理割合ダメージを与える。\n　さらに、アゾールドの物理防御力と魔法防御力\n　を大ダウン。\n・万能人形を召喚し敵全体をノックバックさせる。\n　万能人形は前から１番目のキャラに物理小ダメー\n　ジを与える。\n　万能人形のＨＰが一定値以下になった時、アゾー\n　ルドの物理防御力と魔法防御力を大ダウンさせ\n　る。\n・前から１番目のキャラに物理小ダメージ。", 270, 0, 313800, 1.5, 0, 0, 313800, "アゾールド");
