INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (313900, 1, 3000, 16, "一騎当千、万夫不当の実力を揮う寡黙な剣士。\n覚醒することで、龍は更なる高みへと昇る。\n\n・ユニオンバーストは、敵全体にゼーンの物理\n　攻撃力と残りＨＰに応じた物理割合ダメージ\n　を与える。\n　このスキルによるダメージでは、ＴＰ回復量\n　は減少する。\n・敵全体に物理中ダメージを与え、スタンさせ\n　る。\n・ゼーンのＴＰ上昇、行動速度、物理攻撃力を\n　大アップ。\n　このスキルの効果は発動するたびにアップす\n　る。\n　３回目の発動時は、さらにＴＰ1000アップ。\n・前から１番目のキャラに物理中ダメージを与\n　え、小さく吹き飛ばす。", 270, 0, 313900, 1.0, 0, 313900, 313900, "ゼーン");
