INSERT INTO `v1_fd9b348605bf5fff18c6b8ba6dcc6b806cf8ae3efd4efce8f4c154f09bf151a2` VALUES (0, 1, 3000, 16, "夏休みの間ずっと、目を背けてきた最後の宿題。\nここまで放置してきた後悔が一気に襲い掛かる。\nすべてやり遂げて笑顔で登校できるか！？\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 270, 0, 314006, 1.0, 0, 0, 314006, "夏休み最後まで残りし宿題");
