INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12456, "イエローカード", "イエローカード", 1, "これ、何と言う？", 5, 5, 12489, 5, 12452, 5, "サッカーで悪質な反則に対して示される警告。これが2回出ると、さらに重いレッドカードとなる", 1212, 0, 5, 12525, 12459, 2, "イエロカド", 1);
