INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12403, "えびすてん（ひらがなで）", "", 3, "これ、何と言う？", 4, 4, 12435, 4, 12360, 4, "「七福神」のうち、漁業や商売を司るとされる神様。ニコニコ顔と、釣り竿と鯛を持った姿が有名", 3193, 0, 4, 12377, 12390, 2, "えびすてん", 2);
