INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12414, "読まずに食べた", "読まずに食べた", 3, "この歌の歌詞は？", 6, 4, 12383, 4, 35501, 4, "有名な童謡の歌い出し。♪白ヤギさんからお手紙着いた　黒ヤギさんたら…", 3202, 0, 6, 39135, 12409, 2, "読ま食べた", 1);
