INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12367, "鬼は外、ふくはうち", "", 3, "これ、何と言う？", 4, 4, 12385, 4, 12405, 4, "「節分」で鬼に豆を投げるときに言う、お決まりのかけ声", 3206, 0, 4, 12399, 12358, 2, "ふくはうち", 1);
