INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12358, "とうにゅう", "", 5, "これ、何と言う？", 4, 4, 12358, 4, 12392, 4, "大豆から作られる飲み物。大変体に良く、牛乳の代わりにする人も多い", 5129, 0, 4, 12395, 12421, 2, "とうにゅう", 1);
