INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12502, "ノブレス・オブリージュ", "ノブレス・オブリージュ", 5, "これ、何と言う？", 5, 5, 12517, 5, 12458, 5, "「高貴な身分は社会的義務を伴う」の意味を持つ、かつて貴族階級から生まれた言葉", 5131, 0, 5, 12522, 12472, 2, "オブリジュ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (40644, "緑黄色野菜", "", 5, "これ、何と言う？", 6, 6, 33756, 6, 32209, 6, "ピーマンやカボチャなど、緑色や黄色の野菜の総称。定義上はカロテンを一定以上含む野菜を指す", 5132, 0, 6, 33394, 37326, 2, "緑黄色野菜", 2);
