INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12473, "バスタオル", "", 5, "これ、何と言う？", 5, 5, 12523, 5, 12496, 5, "大きなサイズのタオルで、文字通り風呂上りに身体を乾かすのに使う", 5135, 0, 5, 12479, 12458, 2, "バスタオル", 1);
