INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12400, "やばいですね☆", "", 2, "これ、何と言う？", 4, 4, 12377, 4, 12420, 4, "ペコリーヌの口癖の一つ。良い意味でも悪い意味でもヤバイことが起きたときに言うことが多い", 6164, 1255, 4, 12356, 12391, 1, "やばいです", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12427, "あるじさま", "", 6, "左のキャラは右のキャラを何と呼ぶ？", 4, 4, 12414, 4, 12354, 4, "", 6165, 6165, 4, 12376, 12373, 1, "あるじさま", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12477, "クソメガネ", "", 6, "左のキャラは右のキャラを何と呼ぶ？", 5, 5, 12493, 5, 12463, 5, "", 6166, 6166, 5, 12513, 12460, 1, "クソメガネ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12524, "フレーメン反応", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12501, 5, "猫などの動物が強い匂いを嗅いだときに、目や口を開いて独特な表情をする現象", 6167, 0, 5, 12540, 12513, 2, "フレーメン", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12460, "メガロドン", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12513, 5, "太古の海に存在していた、史上最大のサメ", 6168, 0, 5, 12525, 12489, 2, "メガロドン", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12392, "いときりば", "", 6, "これ、何と言う？", 4, 4, 12400, 4, 12356, 4, "「犬歯」の通称として、「八重歯」以外になじみ深いもの。糸が切れるほど尖っているのが由来", 6169, 0, 4, 12365, 12426, 2, "いときりば", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12493, "カーネーション", "カーネーション", 6, "これ、何と言う？", 5, 5, 12531, 5, 12459, 5, "「母の日」の贈り物としても親しまれる花。赤やピンクの鮮やかな花びらを持つ", 6170, 0, 5, 12471, 12519, 2, "カネション", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12452, "ソメイヨシノ", "", 6, "これ、何と言う？", 5, 5, 12494, 5, 12513, 5, "桜の木の種類として非常にポピュラーな一つ。一般に桜のイメージとして最も浸透している", 6171, 0, 5, 12520, 12471, 2, "メイヨシノ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12521, "ダンデライオン", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12487, 5, "「タンポポ」の英名。とある哺乳類の動物に由来している", 6172, 0, 5, 12452, 12458, 2, "デライオン", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12356, "せいりゅう", "", 6, "これ、何と言う？", 4, 4, 12358, 4, 12379, 4, "「四聖獣」と呼ばれる生き物、朱雀・白虎・玄武、あとは何？（ひらがなで）", 6173, 0, 4, 12426, 12421, 2, "せいりゅう", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (25975, "座敷わらし", "", 6, "これ、何と言う？", 4, 4, 12375, 4, 24231, 6, "小さな子どもの姿で、座敷や土間に現れる妖怪。遭遇した者には幸運が舞い込むとされる", 6174, 0, 6, 12431, 12425, 2, "座敷わらし", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12531, "マンチカン", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12510, 5, "猫の種類の一つ。「小さい」「子ども」という意味を持ち、小柄で脚が短いのが特徴", 6175, 0, 5, 12481, 12459, 2, "マンチカン", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12517, "デュラハン", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12487, 5, "伝承に登場する、首の無い騎士", 6176, 0, 5, 12521, 12495, 2, "デュラハン", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12483, "オッドアイ", "", 6, "これ、何と言う？", 5, 5, 12452, 5, 12458, 5, "両目でそれぞれ瞳の色が異なること。いろいろな動物に見られるが、特に猫に多い", 6177, 0, 5, 12489, 12450, 2, "オッドアイ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12376, "猫じゃらし", "", 6, "これ、何と言う？", 4, 4, 12375, 4, 29483, 4, "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", 6178, 0, 6, 12419, 12425, 2, "猫じゃらし", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12521, "ドラキュラ", "", 6, "これ、何と言う？", 5, 5, 12521, 5, 12489, 5, "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", 6179, 0, 5, 12461, 12517, 2, "ドラキュラ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12467, "フンコロガシ", "", 6, "これ、何と言う？", 5, 5, 12471, 5, 12531, 5, "動物のフンをボール状にして、コロコロ転がすという変わった習性を持つ昆虫", 6180, 0, 5, 12525, 12460, 2, "ンコロガシ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12464, "ラグドール", "", 6, "これ、何と言う？", 5, 5, 12523, 5, 12521, 5, "「ぬいぐるみ」という意味を持つ、猫の種類。抱っこが好きで、毛並みがフサフサなことから", 6181, 0, 5, 12489, 12540, 2, "ラグドール", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12461, "エキゾチック・ショートヘア", "エキゾチック・ショートヘア", 6, "これ、何と言う？", 5, 5, 12463, 5, 12456, 5, "猫の種類の一つ。鼻が低く、ムスッとしたような愛嬌のある顔立ちが特徴的", 6182, 0, 5, 12478, 12481, 2, "エキゾチク", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12375, "でんしょばと", "", 6, "これ、何と言う？", 4, 4, 12392, 4, 12435, 4, "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", 6183, 0, 4, 12423, 12400, 2, "んしょばと", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12507, "ホーホケキョ", "ホーホケキョ", 6, "これ、何と言う？", 5, 5, 12519, 5, 12507, 5, "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", 6184, 0, 5, 12465, 12461, 2, "ホホケキョ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12488, "ホトトギス", "", 6, "これ、何と言う？", 5, 5, 12473, 5, 12507, 5, "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", 6185, 0, 5, 12488, 12462, 2, "ホトトギス", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12499, "ヘビイチゴ", "", 6, "これ、何と言う？", 5, 5, 12468, 5, 12504, 5, "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", 6186, 0, 5, 12452, 12481, 2, "ヘビイチゴ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12412, "松ぼっくり", "", 6, "これ、何と言う？", 4, 4, 12426, 4, 26494, 4, "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", 6187, 0, 6, 12387, 12367, 2, "松ぼっくり", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12478, "アゾールド", "", 6, "このキャラの名前は？", 5, 5, 12489, 5, 12450, 5, "", 6188, 6188, 5, 12540, 12523, 1, "アゾールド", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12490, "ズナちょむ", "", 6, "左のキャラは右のキャラを何と呼ぶ？", 4, 4, 12416, 4, 12474, 5, "", 6189, 6189, 5, 12385, 12423, 1, "ズナちょむ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12457, "スコティッシュフォールド", "", 6, "これ、何と言う？", 5, 5, 12489, 5, 12501, 5, "猫の種類の一つ。垂れた耳が特徴的", 6190, 0, 5, 12540, 12523, 2, "フォールド", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12540, "ゴールデンレトリーバー", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12468, 5, "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", 6191, 0, 5, 12523, 12487, 2, "ゴールデン", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12483, "ドッグラン", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12489, 5, "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", 6192, 0, 5, 12464, 12521, 2, "ドッグラン", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12502, "ラブラドールレトリーバー", "ラブラドールレトリーバー", 6, "これ、何と言う？", 5, 5, 12523, 5, 12521, 5, "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", 6193, 0, 5, 12521, 12489, 2, "ラブラドル", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12540, "マーキング", "", 6, "これ、何と言う？", 5, 5, 12464, 5, 12510, 5, "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", 6194, 0, 5, 12461, 12531, 2, "マーキング", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12356, "たいこ持ち", "", 6, "これ、何と言う？", 4, 6, 12385, 4, 12383, 4, "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", 6195, 0, 4, 12371, 25345, 2, "たいこ持ち", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12522, "キリギリス", "", 6, "これ、何と言う？", 5, 5, 12473, 5, 12461, 5, "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", 6196, 0, 5, 12462, 12522, 2, "キリギリス", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12523, "クルマエビ", "", 6, "これ、何と言う？", 5, 5, 12499, 5, 12463, 5, "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", 6197, 0, 5, 12510, 12456, 2, "クルマエビ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12452, "アオダイショウ", "", 6, "これ、何と言う？", 5, 5, 12454, 5, 12480, 5, "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", 6198, 0, 5, 12471, 12519, 2, "ダイショウ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12381, "しそちょう", "", 6, "これ、何と言う？", 4, 4, 12358, 4, 12375, 4, "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", 6199, 0, 4, 12385, 12423, 2, "しそちょう", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12454, "アウストラロピテクス", "", 6, "これ、何と言う？", 5, 5, 12521, 5, 12450, 5, "「最古の人類」とされている種類の猿人", 6200, 0, 5, 12473, 12488, 2, "アウストラ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12517, "キュウカンチョウ", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12461, 5, "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", 6201, 0, 5, 12454, 12459, 2, "キュウカン", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12483, "オットセイ", "", 6, "これ、何と言う？", 5, 5, 12452, 5, 12458, 5, "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", 6202, 0, 5, 12488, 12475, 2, "オットセイ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12472, "アジアゾウ", "", 6, "これ、何と言う？", 5, 5, 12454, 5, 12450, 5, "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", 6203, 0, 5, 12450, 12478, 2, "アジアゾウ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12491, "ベニシジミ", "", 6, "これ、何と言う？", 5, 5, 12511, 5, 12505, 5, "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", 6204, 0, 5, 12471, 12472, 2, "ベニシジミ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12517, "ジュウサンホシテントウ", "", 6, "これ、何と言う？", 5, 5, 12531, 5, 12472, 5, "テントウムシの一種。身体に13個の点々模様がある", 6205, 0, 5, 12454, 12469, 2, "ジュウサン", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12472, "ニジュウヤホシテントウ", "", 6, "これ、何と言う？", 5, 5, 12516, 5, 12491, 5, "テントウムシの一種。身体に28個の点々模様がある", 6206, 0, 5, 12517, 12454, 2, "ニジュウヤ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12491, "アニサキス", "", 6, "これ、何と言う？", 5, 5, 12473, 5, 12450, 5, "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", 6207, 0, 5, 12469, 12461, 2, "アニサキス", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12383, "わたりどり", "", 6, "これ、何と言う？", 4, 4, 12426, 4, 12431, 4, "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", 6208, 0, 4, 12426, 12393, 2, "わたりどり", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12531, "フンボルトペンギン", "", 6, "これ、何と言う？", 5, 5, 12488, 5, 12501, 5, "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", 6209, 0, 5, 12508, 12523, 2, "フンボルト", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12508, "エボシドリ", "", 6, "これ、何と言う？", 5, 5, 12522, 5, 12456, 5, "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", 6210, 0, 5, 12471, 12489, 2, "エボシドリ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12470, "ネザーランドドワーフ", "ネザーランドドワーフ", 6, "これ、何と言う？", 5, 5, 12489, 5, 12493, 5, "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", 6211, 0, 5, 12521, 12531, 2, "ネザランド", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12540, "ホーランドロップ", "", 6, "これ、何と言う？", 5, 5, 12489, 5, 12507, 5, "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", 6212, 0, 5, 12521, 12531, 2, "ホーランド", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12455, "フェレット", "", 6, "これ、何と言う？", 5, 5, 12488, 5, 12501, 5, "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", 6213, 0, 5, 12524, 12483, 2, "フェレット", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12531, "マントヒヒ", "", 6, "これ、何と言う？", 5, 5, 12498, 5, 12510, 5, "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", 6214, 0, 5, 12488, 12498, 2, "マントヒヒ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12525, "シロナガスクジラ", "", 6, "これ、何と言う？", 5, 5, 12473, 5, 12471, 5, "最も大きい動物としてよく知られる種類のクジラ", 6215, 0, 5, 12490, 12460, 2, "シロナガス", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12525, "シロイルカ", "", 6, "これ、何と言う？", 5, 5, 12459, 5, 12471, 5, "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", 6216, 0, 5, 12452, 12523, 2, "シロイルカ", 3);
