INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12467, "さそり＝スコーピオン", "さそり＝スコーピオン", 6, "これ、何と言う？", 5, 5, 12531, 5, 12473, 5, "カタカナでの呼び方は？", 6218, 0, 5, 12500, 12458, 2, "スコピオン", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12363, "ちからこぶ", "", 6, "これ、何と言う？", 4, 4, 12406, 4, 12385, 4, "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", 6219, 0, 4, 12425, 12371, 2, "ちからこぶ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12427, "さるかに合戦", "", 6, "これ、何と言う？", 4, 4, 21512, 6, 12373, 4, "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", 6220, 0, 4, 12363, 12395, 2, "さるかに合", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12531, "♪サル、ゴリラ、チンパンジー", "", 6, "これ、何と言う？", 5, 5, 12472, 5, 12481, 5, "よく親しまれた替え歌の歌詞。ある動物の名前が入る", 6221, 0, 5, 12497, 12531, 2, "チンパンジ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12458, "アオイソメ", "", 6, "これ、何と言う？", 5, 5, 12513, 5, 12450, 5, "釣り人には釣り餌として非常におなじみの、ミミズのような虫", 6222, 0, 5, 12452, 12477, 2, "アオイソメ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12513, "鋼メンタル", "", 6, "これ、何と言う？", 5, 5, 12523, 5, 37628, 5, "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", 6223, 0, 6, 12531, 12479, 2, "鋼メンタル", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12478, "マゾヒスト", "", 6, "これ、何と言う？", 5, 5, 12488, 5, 12510, 5, "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", 6224, 6224, 5, 12498, 12473, 1, "マゾヒスト", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, "ケツァルコアトルス", "", 6, "これ、何と言う？", 5, 5, 12473, 5, 12467, 5, "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", 6225, 0, 5, 12488, 12523, 2, "コアトルス", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12490, "アナツバメ", "", 6, "これ、何と言う？", 5, 5, 12513, 5, 12450, 5, "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", 6226, 0, 5, 12484, 12496, 2, "アナツバメ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12531, "タンチョウヅル", "", 6, "これ、何と言う？", 5, 5, 12454, 5, 12479, 5, "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", 6227, 0, 5, 12481, 12519, 2, "タンチョウ", 3);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12522, "ルリタテハ", "", 6, "これ、何と言う？", 5, 5, 12495, 5, 12523, 5, "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", 6228, 0, 5, 12479, 12486, 2, "ルリタテハ", 3);
