INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12502, "アブラゼミ", "", 6, "これ、何と言う？", 5, 5, 12511, 5, 12450, 5, "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", 6232, 0, 5, 12521, 12476, 2, "アブラゼミ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12461, "アキレス腱", "", 6, "これ、何と言う？", 5, 5, 33137, 6, 12450, 5, "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", 6233, 0, 5, 12524, 12473, 2, "アキレス腱", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12523, "ナルシスト", "", 6, "これ、何と言う？", 5, 5, 12488, 5, 12490, 5, "自己愛が非常に強く、自分に陶酔した人、英語にすると？", 6234, 6234, 5, 12471, 12473, 1, "ナルシスト", 1);
