INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12452, "ハイビスカス", "", 6, "これ、何と言う？", 5, 5, 12459, 5, 12495, 5, "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", 6242, 0, 5, 12499, 12473, 1, "ハイビスカ", 1);
