INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12471, "ニシキゴイ", "", 6, "これ、何と言う？", 5, 5, 12452, 5, 12491, 5, "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", 6244, 0, 5, 12461, 12468, 2, "ニシキゴイ", 2);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12469, "レッサーパンダ", "レッサーパンダ", 6, "これ、何と言う？", 5, 5, 12480, 5, 12524, 5, "「小さなパンダ」が由来の、動物園の人気者", 6245, 0, 5, 12497, 12531, 2, "レサパンダ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12490, "アナコンダ", "", 6, "これ、何と言う？", 5, 5, 12480, 5, 12450, 5, "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", 6246, 0, 5, 12467, 12531, 2, "アナコンダ", 1);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12521, "タランチュラ", "", 6, "これ、何と言う？", 5, 5, 12517, 5, 12479, 5, "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", 6247, 0, 5, 12531, 12481, 2, "タランチュ", 1);
