INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12512, "ハムスター", "", 6, "これ、何と言う？", 5, 5, 12540, 5, 12495, 5, "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", 6249, 0, 5, 12473, 12479, 2, "ハムスター", 1);
