INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12420, 4, "これ、何と言う？", 12400, 1, 12377, 4, 12391, "ペコリーヌの口癖の一つ。良い意味でも悪い意味でもヤバイことが起きたときに言うことが多い", "やばいですね☆", "", 1255, 2, 4, 4, "やばいです", 6164, 2, 12356, 4);
