INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12463, 5, "左のキャラは右のキャラを何と呼ぶ？", 12477, 1, 12493, 5, 12460, "", "クソメガネ", "", 6166, 6, 5, 5, "クソメガネ", 6166, 2, 12513, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12501, 5, "これ、何と言う？", 12524, 2, 12531, 5, 12513, "猫などの動物が強い匂いを嗅いだときに、目や口を開いて独特な表情をする現象", "フレーメン反応", "", 0, 6, 5, 5, "フレーメン", 6167, 3, 12540, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12513, 5, "これ、何と言う？", 12460, 2, 12531, 5, 12489, "太古の海に存在していた、史上最大のサメ", "メガロドン", "", 0, 6, 5, 5, "メガロドン", 6168, 3, 12525, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12356, 4, "これ、何と言う？", 12392, 2, 12400, 4, 12426, "「犬歯」の通称として、「八重歯」以外になじみ深いもの。糸が切れるほど尖っているのが由来", "いときりば", "", 0, 6, 4, 4, "いときりば", 6169, 3, 12365, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12459, 5, "これ、何と言う？", 12493, 2, 12531, 5, 12519, "「母の日」の贈り物としても親しまれる花。赤やピンクの鮮やかな花びらを持つ", "カーネーション", "カーネーション", 0, 6, 5, 5, "カネション", 6170, 1, 12471, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12513, 5, "これ、何と言う？", 12452, 2, 12494, 5, 12471, "桜の木の種類として非常にポピュラーな一つ。一般に桜のイメージとして最も浸透している", "ソメイヨシノ", "", 0, 6, 5, 5, "メイヨシノ", 6171, 2, 12520, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12487, 5, "これ、何と言う？", 12521, 2, 12531, 5, 12458, "「タンポポ」の英名。とある哺乳類の動物に由来している", "ダンデライオン", "", 0, 6, 5, 5, "デライオン", 6172, 3, 12452, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12379, 4, "これ、何と言う？", 12356, 2, 12358, 4, 12421, "「四聖獣」と呼ばれる生き物、朱雀・白虎・玄武、あとは何？（ひらがなで）", "せいりゅう", "", 0, 6, 4, 4, "せいりゅう", 6173, 3, 12426, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (24231, 4, "これ、何と言う？", 25975, 2, 12375, 6, 12425, "小さな子どもの姿で、座敷や土間に現れる妖怪。遭遇した者には幸運が舞い込むとされる", "座敷わらし", "", 0, 6, 6, 4, "座敷わらし", 6174, 1, 12431, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12510, 5, "これ、何と言う？", 12531, 2, 12531, 5, 12459, "猫の種類の一つ。「小さい」「子ども」という意味を持ち、小柄で脚が短いのが特徴", "マンチカン", "", 0, 6, 5, 5, "マンチカン", 6175, 2, 12481, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12487, 5, "これ、何と言う？", 12517, 2, 12531, 5, 12495, "伝承に登場する、首の無い騎士", "デュラハン", "", 0, 6, 5, 5, "デュラハン", 6176, 2, 12521, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12458, 5, "これ、何と言う？", 12483, 2, 12452, 5, 12450, "両目でそれぞれ瞳の色が異なること。いろいろな動物に見られるが、特に猫に多い", "オッドアイ", "", 0, 6, 5, 5, "オッドアイ", 6177, 2, 12489, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (29483, 4, "これ、何と言う？", 12376, 2, 12375, 6, 12425, "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", "猫じゃらし", "", 0, 6, 4, 4, "猫じゃらし", 6178, 1, 12419, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12489, 5, "これ、何と言う？", 12521, 2, 12521, 5, 12517, "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", "ドラキュラ", "", 0, 6, 5, 5, "ドラキュラ", 6179, 1, 12461, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12531, 5, "これ、何と言う？", 12467, 2, 12471, 5, 12460, "動物のフンをボール状にして、コロコロ転がすという変わった習性を持つ昆虫", "フンコロガシ", "", 0, 6, 5, 5, "ンコロガシ", 6180, 1, 12525, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12521, 5, "これ、何と言う？", 12464, 2, 12523, 5, 12540, "「ぬいぐるみ」という意味を持つ、猫の種類。抱っこが好きで、毛並みがフサフサなことから", "ラグドール", "", 0, 6, 5, 5, "ラグドール", 6181, 3, 12489, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12456, 5, "これ、何と言う？", 12461, 2, 12463, 5, 12481, "猫の種類の一つ。鼻が低く、ムスッとしたような愛嬌のある顔立ちが特徴的", "エキゾチック・ショートヘア", "エキゾチック・ショートヘア", 0, 6, 5, 5, "エキゾチク", 6182, 3, 12478, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12435, 4, "これ、何と言う？", 12375, 2, 12392, 4, 12400, "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", "でんしょばと", "", 0, 6, 4, 4, "んしょばと", 6183, 2, 12423, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12507, 5, "これ、何と言う？", 12507, 2, 12519, 5, 12461, "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", "ホーホケキョ", "ホーホケキョ", 0, 6, 5, 5, "ホホケキョ", 6184, 2, 12465, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12507, 5, "これ、何と言う？", 12488, 2, 12473, 5, 12462, "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", "ホトトギス", "", 0, 6, 5, 5, "ホトトギス", 6185, 1, 12488, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12504, 5, "これ、何と言う？", 12499, 2, 12468, 5, 12481, "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", "ヘビイチゴ", "", 0, 6, 5, 5, "ヘビイチゴ", 6186, 2, 12452, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (26494, 4, "これ、何と言う？", 12412, 2, 12426, 6, 12367, "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", "松ぼっくり", "", 0, 6, 4, 4, "松ぼっくり", 6187, 2, 12387, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "このキャラの名前は？", 12478, 1, 12489, 5, 12523, "", "アゾールド", "", 6188, 6, 5, 5, "アゾールド", 6188, 1, 12540, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12474, 4, "左のキャラは右のキャラを何と呼ぶ？", 12490, 1, 12416, 5, 12423, "", "ズナちょむ", "", 6189, 6, 5, 4, "ズナちょむ", 6189, 3, 12385, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12501, 5, "これ、何と言う？", 12457, 2, 12489, 5, 12523, "猫の種類の一つ。垂れた耳が特徴的", "スコティッシュフォールド", "", 0, 6, 5, 5, "フォールド", 6190, 2, 12540, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12468, 5, "これ、何と言う？", 12540, 2, 12531, 5, 12487, "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", "ゴールデンレトリーバー", "", 0, 6, 5, 5, "ゴールデン", 6191, 1, 12523, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12489, 5, "これ、何と言う？", 12483, 2, 12531, 5, 12521, "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", "ドッグラン", "", 0, 6, 5, 5, "ドッグラン", 6192, 2, 12464, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12521, 5, "これ、何と言う？", 12502, 2, 12523, 5, 12489, "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", "ラブラドールレトリーバー", "ラブラドールレトリーバー", 0, 6, 5, 5, "ラブラドル", 6193, 1, 12521, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12510, 5, "これ、何と言う？", 12540, 2, 12464, 5, 12531, "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", "マーキング", "", 0, 6, 5, 5, "マーキング", 6194, 1, 12461, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12383, 4, "これ、何と言う？", 12356, 2, 12385, 4, 25345, "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", "たいこ持ち", "", 0, 6, 4, 6, "たいこ持ち", 6195, 2, 12371, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12461, 5, "これ、何と言う？", 12522, 2, 12473, 5, 12522, "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", "キリギリス", "", 0, 6, 5, 5, "キリギリス", 6196, 1, 12462, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12463, 5, "これ、何と言う？", 12523, 2, 12499, 5, 12456, "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", "クルマエビ", "", 0, 6, 5, 5, "クルマエビ", 6197, 1, 12510, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12480, 5, "これ、何と言う？", 12452, 2, 12454, 5, 12519, "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", "アオダイショウ", "", 0, 6, 5, 5, "ダイショウ", 6198, 3, 12471, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12375, 4, "これ、何と言う？", 12381, 2, 12358, 4, 12423, "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", "しそちょう", "", 0, 6, 4, 4, "しそちょう", 6199, 2, 12385, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12454, 2, 12521, 5, 12488, "「最古の人類」とされている種類の猿人", "アウストラロピテクス", "", 0, 6, 5, 5, "アウストラ", 6200, 2, 12473, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12461, 5, "これ、何と言う？", 12517, 2, 12531, 5, 12459, "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", "キュウカンチョウ", "", 0, 6, 5, 5, "キュウカン", 6201, 1, 12454, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12458, 5, "これ、何と言う？", 12483, 2, 12452, 5, 12475, "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", "オットセイ", "", 0, 6, 5, 5, "オットセイ", 6202, 2, 12488, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12472, 2, 12454, 5, 12478, "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", "アジアゾウ", "", 0, 6, 5, 5, "アジアゾウ", 6203, 1, 12450, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12505, 5, "これ、何と言う？", 12491, 2, 12511, 5, 12472, "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", "ベニシジミ", "", 0, 6, 5, 5, "ベニシジミ", 6204, 3, 12471, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12472, 5, "これ、何と言う？", 12517, 2, 12531, 5, 12469, "テントウムシの一種。身体に13個の点々模様がある", "ジュウサンホシテントウ", "", 0, 6, 5, 5, "ジュウサン", 6205, 2, 12454, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12491, 5, "これ、何と言う？", 12472, 2, 12516, 5, 12454, "テントウムシの一種。身体に28個の点々模様がある", "ニジュウヤホシテントウ", "", 0, 6, 5, 5, "ニジュウヤ", 6206, 3, 12517, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12491, 2, 12473, 5, 12461, "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", "アニサキス", "", 0, 6, 5, 5, "アニサキス", 6207, 2, 12469, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12431, 4, "これ、何と言う？", 12383, 2, 12426, 4, 12393, "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", "わたりどり", "", 0, 6, 4, 4, "わたりどり", 6208, 1, 12426, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12501, 5, "これ、何と言う？", 12531, 2, 12488, 5, 12523, "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", "フンボルトペンギン", "", 0, 6, 5, 5, "フンボルト", 6209, 3, 12508, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12456, 5, "これ、何と言う？", 12508, 2, 12522, 5, 12489, "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", "エボシドリ", "", 0, 6, 5, 5, "エボシドリ", 6210, 3, 12471, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12493, 5, "これ、何と言う？", 12470, 2, 12489, 5, 12531, "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", "ネザーランドドワーフ", "ネザーランドドワーフ", 0, 6, 5, 5, "ネザランド", 6211, 3, 12521, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12507, 5, "これ、何と言う？", 12540, 2, 12489, 5, 12531, "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", "ホーランドロップ", "", 0, 6, 5, 5, "ホーランド", 6212, 3, 12521, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12501, 5, "これ、何と言う？", 12455, 2, 12488, 5, 12483, "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", "フェレット", "", 0, 6, 5, 5, "フェレット", 6213, 2, 12524, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12510, 5, "これ、何と言う？", 12531, 2, 12498, 5, 12498, "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", "マントヒヒ", "", 0, 6, 5, 5, "マントヒヒ", 6214, 2, 12488, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12471, 5, "これ、何と言う？", 12525, 2, 12473, 5, 12460, "最も大きい動物としてよく知られる種類のクジラ", "シロナガスクジラ", "", 0, 6, 5, 5, "シロナガス", 6215, 2, 12490, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12471, 5, "これ、何と言う？", 12525, 2, 12459, 5, 12523, "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", "シロイルカ", "", 0, 6, 5, 5, "シロイルカ", 6216, 3, 12452, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12375, 4, "これ、何と言う？", 12429, 2, 12366, 4, 12373, "神話に登場する、有名な動物", "いなばのしろうさぎ", "", 0, 6, 4, 4, "しろうさぎ", 6217, 2, 12358, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12473, 5, "これ、何と言う？", 12467, 2, 12531, 5, 12458, "カタカナでの呼び方は？", "さそり＝スコーピオン", "さそり＝スコーピオン", 0, 6, 5, 5, "スコピオン", 6218, 1, 12500, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12385, 4, "これ、何と言う？", 12363, 2, 12406, 4, 12371, "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", "ちからこぶ", "", 0, 6, 4, 4, "ちからこぶ", 6219, 2, 12425, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12373, 4, "これ、何と言う？", 12427, 2, 21512, 4, 12395, "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", "さるかに合戦", "", 0, 6, 4, 4, "さるかに合", 6220, 2, 12363, 6);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12481, 5, "これ、何と言う？", 12531, 2, 12472, 5, 12531, "よく親しまれた替え歌の歌詞。ある動物の名前が入る", "♪サル、ゴリラ、チンパンジー", "", 0, 6, 5, 5, "チンパンジ", 6221, 1, 12497, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12458, 2, 12513, 5, 12477, "釣り人には釣り餌として非常におなじみの、ミミズのような虫", "アオイソメ", "", 0, 6, 5, 5, "アオイソメ", 6222, 3, 12452, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (37628, 5, "これ、何と言う？", 12513, 2, 12523, 6, 12479, "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", "鋼メンタル", "", 0, 6, 5, 5, "鋼メンタル", 6223, 3, 12531, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12510, 5, "これ、何と言う？", 12478, 1, 12488, 5, 12473, "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", "マゾヒスト", "", 6224, 6, 5, 5, "マゾヒスト", 6224, 3, 12498, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12467, 5, "これ、何と言う？", 12450, 2, 12473, 5, 12523, "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", "ケツァルコアトルス", "", 0, 6, 5, 5, "コアトルス", 6225, 3, 12488, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12490, 2, 12513, 5, 12496, "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", "アナツバメ", "", 0, 6, 5, 5, "アナツバメ", 6226, 3, 12484, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12479, 5, "これ、何と言う？", 12531, 2, 12454, 5, 12519, "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", "タンチョウヅル", "", 0, 6, 5, 5, "タンチョウ", 6227, 3, 12481, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12523, 5, "これ、何と言う？", 12522, 2, 12495, 5, 12486, "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", "ルリタテハ", "", 0, 6, 5, 5, "ルリタテハ", 6228, 3, 12479, 5);
