INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12493, 4, "このパネルの「プリコネ読み」は？", 12467, 1, 12417, 5, 12377, "", "ネコむすめ", "", 6230, 6, 5, 4, "ネコむすめ", 6230, 3, 12416, 4);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (32118, 6, "これ、何と言う？", 28357, 2, 31278, 6, 24807, "数が減少し、絶滅の危機に晒されている生物", "絶滅危惧種", "", 0, 6, 6, 6, "絶滅危惧種", 6231, 2, 21361, 6);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12502, 2, 12511, 5, 12476, "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", "アブラゼミ", "", 0, 6, 5, 5, "アブラゼミ", 6232, 1, 12521, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12461, 2, 33137, 5, 12473, "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", "アキレス腱", "", 0, 6, 5, 5, "アキレス腱", 6233, 1, 12524, 6);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12490, 5, "これ、何と言う？", 12523, 1, 12488, 5, 12473, "自己愛が非常に強く、自分に陶酔した人、英語にすると？", "ナルシスト", "", 6234, 6, 5, 5, "ナルシスト", 6234, 1, 12471, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12384, 4, "左のキャラは右のキャラを何と呼ぶ？", 12435, 1, 12358, 4, 12423, "", "だんちょう", "", 6235, 6, 4, 4, "だんちょう", 6235, 3, 12385, 4);
