INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12420, 4, "これ、何と言う？", 12383, 2, 12377, 4, 12425, "「古事記」などに登場する、三本足の神聖なカラス", "やたがらす", "", 0, 6, 4, 4, "やたがらす", 6237, 3, 12364, 4);
