INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12487, 5, "これ、何と言う？", 12451, 2, 12540, 5, 12521, "カジノで、ルーレットを回したりカードを配ったりとゲームの進行を行う役目", "ディーラー", "", 0, 6, 5, 5, "ディーラー", 6239, 2, 12540, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12456, 5, "これ、何と言う？", 12531, 2, 12473, 5, 12524, "「皇帝」は「エンペラー」。では「女帝」は？", "エンプレス", "", 0, 6, 5, 5, "エンプレス", 6240, 3, 12503, 5);
