INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12495, 5, "これ、何と言う？", 12452, 1, 12459, 5, 12473, "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", "ハイビスカス", "", 0, 6, 5, 5, "ハイビスカ", 6242, 1, 12499, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12458, 5, "これ、何と言う？", 12454, 2, 12503, 5, 12473, "「ひとだま」と呼ばれる超常現象、英語で言うと？", "ウィル・オ・ウィスプ", "ウィル・オ・ウィスプ", 0, 6, 5, 5, "オウィスプ", 6243, 3, 12451, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12491, 5, "これ、何と言う？", 12471, 2, 12452, 5, 12468, "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", "ニシキゴイ", "", 0, 6, 5, 5, "ニシキゴイ", 6244, 2, 12461, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12524, 5, "これ、何と言う？", 12469, 2, 12480, 5, 12531, "「小さなパンダ」が由来の、動物園の人気者", "レッサーパンダ", "レッサーパンダ", 0, 6, 5, 5, "レサパンダ", 6245, 1, 12497, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12450, 5, "これ、何と言う？", 12490, 2, 12480, 5, 12531, "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", "アナコンダ", "", 0, 6, 5, 5, "アナコンダ", 6246, 1, 12467, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12479, 5, "これ、何と言う？", 12521, 2, 12517, 5, 12481, "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", "タランチュラ", "", 0, 6, 5, 5, "タランチュ", 6247, 1, 12531, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12473, 5, "これ、何と言う？", 12452, 2, 12454, 5, 12517, "「牛」の一種で、地域によって家畜としてもなじみ深い動物。その名の通り、水辺を好む", "スイギュウ", "", 0, 6, 5, 5, "スイギュウ", 6248, 1, 12462, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12495, 5, "これ、何と言う？", 12512, 2, 12540, 5, 12479, "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", "ハムスター", "", 0, 6, 5, 5, "ハムスター", 6249, 1, 12473, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12495, 5, "これ、何と言う？", 12522, 2, 12511, 5, 12474, "身を守るためのトゲトゲの体毛が非常に特徴的な動物。愛好家も多く、英名は「ヘッジホッグ」", "ハリネズミ", "", 0, 6, 5, 5, "ハリネズミ", 6250, 1, 12493, 5);
INSERT INTO `v1_fff054c48bdfa6876248bf936c5d911316b5163466bb17d0ef7bdeeef7583521` VALUES (12498, 5, "これ、何と言う？", 12519, 2, 12531, 5, 12514, "猛毒を持つタコ。蛍光色のヒョウ柄の模様が、恐ろしさを助長している", "ヒョウモンダコ", "", 0, 6, 5, 5, "ヒョウモン", 6251, 3, 12454, 5);
